import { useState } from "react";
import "./App.css";
import Slider from "./Slider";
import Sidebaritem from "./Sidebaritem";

const defualtoptions = [
  {
    name: "Brightness",
    property: "brightness",
    value: 100,
    range: {
      min: 0,
      max: 200,
    },
    unit: "%",
  },
  {
    name: "Saturation",
    property: "saturation",
    value: 100,
    range: {
      min: 0,
      max: 200,
    },
    unit: "%",
  },
  {
    name: "Contrast",
    property: "contrast",
    value: 100,
    range: {
      min: 0,
      max: 200,
    },
    unit: "%",
  },
  {
    name: "Grayscale",
    property: "grayscale",
    value: 0,
    range: {
      min: 0,
      max: 100,
    },
    unit: "%",
  },
  {
    name: "Sepia",
    property: "sepia",
    value: 0,
    range: {
      min: 0,
      max: 100,
    },
    unit: "%",
  },
  {
    name: "Hue-Rotate",
    property: "hue-rotate",
    value: 0,
    range: {
      min: 0,
      max: 360,
    },
    unit: "deg",
  },
  {
    name: "Blur",
    property: "blur",
    value: 0,
    range: {
      min: 0,
      max: 20,
    },
    unit: "px",
  },
];
function App() {
  const [selectOptionsIndex, setselectOptionsIndex] = useState(0);
  const [option, setoption] = useState(defualtoptions);
  const selectedOption = option[selectOptionsIndex];
  console.log(option.property);
  return (
    <div className="container">
      <div className="main-image">Image</div>
      <div className="sidebar">
        {option.map((options, index) => {
          return (
            <Sidebaritem
              key={index}
              name={option.name}
              active={index === selectOptionsIndex}
              handleclick={() => selectOptionsIndex(index)}
            />
          );
        })}
      </div>
      <Slider />
    </div>
  );
}

export default App;
